import React, { useState } from "react";
import axios from "axios";

const styles = {
  container: {
    maxWidth: '800px',
    margin: '0 auto',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    backgroundColor: '#1e1e1e',
    color: '#ffffff',
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0,0,0,0.5)',
  },
  title: {
    textAlign: 'center',
    color: '#00ff00',
    marginBottom: '20px',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
  },
  textarea: {
    backgroundColor: '#2e2e2e',
    border: '1px solid #00ff00',
    borderRadius: '5px',
    color: '#ffffff',
    padding: '10px',
    marginBottom: '10px',
    resize: 'vertical',
  },
  button: {
    backgroundColor: '#00ff00',
    color: '#000000',
    border: 'none',
    borderRadius: '5px',
    padding: '10px 20px',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  },
  buttonHover: {
    backgroundColor: '#00cc00',
  },
  response: {
    marginTop: '20px',
    backgroundColor: '#2e2e2e',
    borderRadius: '5px',
    padding: '15px',
  },
  responseTitle: {
    color: '#00ff00',
    marginBottom: '10px',
  },
  code: {
    backgroundColor: '#3e3e3e',
    borderRadius: '5px',
    padding: '15px',
    overflowX: 'auto',
    fontFamily: 'monospace',
    whiteSpace: 'pre-wrap',
  },
};

function Chat() {
  const [input, setInput] = useState("");
  const [response, setResponse] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isHovered, setIsHovered] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      const res = await axios.post("/api/chat", { message: input });
      setResponse(res.data.response);
      setInput("");
    } catch (error) {
      console.error("Detailed frontend error:", error);
      if (error.response) {
        console.error("Error response data:", error.response.data);
        setResponse(error.response.data.error || "An error occurred while generating code.");
      } else if (error.request) {
        console.error("Error request:", error.request);
        setResponse("No response received from the server.");
      } else {
        console.error("Error message:", error.message);
        setResponse("An error occurred while setting up the request.");
      }
    }
    setIsLoading(false);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>AI Code Generator</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <textarea
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Enter your code request"
          rows="4"
          style={styles.textarea}
        />
        <button 
          type="submit" 
          disabled={isLoading}
          style={{...styles.button, ...(isHovered ? styles.buttonHover : {})}}
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => setIsHovered(false)}
        >
          {isLoading ? "Generating..." : "Generate Code"}
        </button>
      </form>
      {response && (
        <div style={styles.response}>
          <h3 style={styles.responseTitle}>Generated Code:</h3>
          <pre style={styles.code}>{response}</pre>
        </div>
      )}
    </div>
  );
}

export default Chat;
