import React, { useState } from "react";
import axios from "axios";

const styles = {
  container: {
    maxWidth: '800px',
    margin: '0 auto',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    backgroundColor: '#1e1e1e',
    color: '#ffffff',
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0,0,0,0.5)',
  },
  title: {
    textAlign: 'center',
    color: '#00ff00',
    marginBottom: '20px',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
  },
  textarea: {
    backgroundColor: '#2e2e2e',
    border: '1px solid #00ff00',
    borderRadius: '5px',
    color: '#ffffff',
    padding: '10px',
    marginBottom: '10px',
    resize: 'vertical',
  },
  button: {
    backgroundColor: '#00ff00',
    color: '#000000',
    border: 'none',
    borderRadius: '5px',
    padding: '10px 20px',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  },
  buttonHover: {
    backgroundColor: '#00cc00',
  },
  response: {
    marginTop: '20px',
    backgroundColor: '#2e2e2e',
    borderRadius: '5px',
    padding: '15px',
  },
  responseTitle: {
    color: '#00ff00',
    marginBottom: '10px',
  },
  code: {
    backgroundColor: '#3e3e3e',
    borderRadius: '5px',
    padding: '15px',
    overflowX: 'auto',
    fontFamily: 'monospace',
    whiteSpace: 'pre-wrap',
  },
  responseHeader: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '10px',
  },
  copyButton: {
    backgroundColor: '#00ff00',
    color: '#000000',
    border: 'none',
    borderRadius: '5px',
    padding: '5px 10px',
    cursor: 'pointer',
    fontSize: '0.9em',
    transition: 'background-color 0.3s',
  },
  copyButtonHover: {
    backgroundColor: '#00cc00',
  },
  wikipediaResponse: {
    backgroundColor: '#2e2e2e',
    borderRadius: '5px',
    padding: '15px',
    marginBottom: '15px',
  },
  wikipediaTitle: {
    color: '#4da6ff',
    marginBottom: '10px',
  },
};

function Chat() {
  const [input, setInput] = useState("");
  const [response, setResponse] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isHovered, setIsHovered] = useState(false);
  const [isCopyHovered, setIsCopyHovered] = useState(false);
  const [copySuccess, setCopySuccess] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setResponse(""); // Clear previous response
    try {
      const res = await axios.post("/api/chat", { message: input });
      setResponse(res.data.response);
      setInput("");
    } catch (error) {
      console.error("Detailed frontend error:", error);
      if (error.response) {
        console.error("Error response data:", error.response.data);
        setResponse(error.response.data.error || "An error occurred while generating a response.");
      } else if (error.request) {
        console.error("Error request:", error.request);
        setResponse("No response received from the server.");
      } else {
        console.error("Error message:", error.message);
        setResponse("An error occurred while setting up the request.");
      }
    }
    setIsLoading(false);
  };

  const handleCopy = () => {
    navigator.clipboard.writeText(response).then(() => {
      setCopySuccess(true);
      setTimeout(() => setCopySuccess(false), 2000);
    }, (err) => {
      console.error('Failed to copy text: ', err);
    });
  };

  const renderResponse = () => {
    if (response.startsWith("Wikipedia: ")) {
      const wikiResponse = response.replace("Wikipedia: ", "");
      return (
        <div style={styles.wikipediaResponse}>
          <h3 style={styles.wikipediaTitle}>Wikipedia Summary:</h3>
          <p>{wikiResponse}</p>
        </div>
      );
    } else {
      return <pre style={styles.code}>{response}</pre>;
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>AI Assistant & Wikipedia</h2>
      <form onSubmit={handleSubmit} style={styles.form}>
        <textarea
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Ask a question about anything, request code, or inquire about web development and cybersecurity"
          rows="4"
          style={styles.textarea}
        />
        <button 
          type="submit" 
          disabled={isLoading}
          style={{...styles.button, ...(isHovered ? styles.buttonHover : {})}}
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => setIsHovered(false)}
        >
          {isLoading ? "Searching..." : "Submit"}
        </button>
      </form>
      {response && (
        <div style={styles.response}>
          <div style={styles.responseHeader}>
            <h3 style={styles.responseTitle}>Response:</h3>
            <button
              onClick={handleCopy}
              style={{
                ...styles.copyButton,
                ...(isCopyHovered ? styles.copyButtonHover : {})
              }}
              onMouseEnter={() => setIsCopyHovered(true)}
              onMouseLeave={() => setIsCopyHovered(false)}
            >
              {copySuccess ? "Copied!" : "Copy Response"}
            </button>
          </div>
          {renderResponse()}
        </div>
      )}
    </div>
  );
}

export default Chat;
